mapper.insert:
insert into book(
	book_id, book_title, book_author, book_publication_date, 
	book_price, book_publisher, book_page_count, book_genre
)values(
	book_seq.nextval, #{bookTitle}, #{bookAuthor}, #{bookPublicationDate}, 
	#{bookPrice}, #{bookPublisher}, #{bookPageCount}, #{bookGenre}
)

mapper.update:
update book set
book_id = #{dto.bookId],
book_title = #{dto.bookTitle}, book_author = #{dto.bookAuthor}, book_publication_date = #{dto.bookPublicationDate}, book_price = #{dto.bookPrice}, book_publisher = #{dto.bookPublisher}, book_page_count = #{dto.bookPageCount}, book_genre = #{dto.bookGenre} where book_id = #{bookId}

update book
book_title = #{dto.bookTitle}, book_author = #{dto.bookAuthor}, book_publication_date = #{dto.bookPublicationDate}, book_price = #{dto.bookPrice}, book_publisher = #{dto.bookPublisher}, book_page_count = #{dto.bookPageCount}, book_genre = #{dto.bookGenre},
where book_id = #{bookId}
